import React, { useEffect, useState } from "react";
import axios from "../api/axios";

import {
  DndContext,
  closestCenter,
  PointerSensor,
  useSensor,
  useSensors,
} from "@dnd-kit/core";
import {
  arrayMove,
  SortableContext,
  useSortable,
  verticalListSortingStrategy,
} from "@dnd-kit/sortable";
import { CSS } from "@dnd-kit/utilities";

// Single sortable item component
function SortableItem({ field }) {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
  } = useSortable({ id: field.id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
    padding: "12px",
    border: "1px solid #ddd",
    borderRadius: "8px",
    marginBottom: "10px",
    backgroundColor: "#f9f9f9",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    cursor: "grab",
  };

  return (
    <div ref={setNodeRef} style={style} {...attributes} {...listeners}>
      <span>{field.label}</span>
      <small className="text-muted">{field.input_type}</small>
    </div>
  );
}

// Main component
export default function FieldBuilder() {
  const [fields, setFields] = useState([]);

  // Fetch existing fields on load
  useEffect(() => {
    axios.get("/employees/fields/")
      .then((res) => setFields(res.data))
      .catch((err) => console.error("Failed to fetch fields:", err));
  }, []);

  // Required for DnD-kit
  const sensors = useSensors(useSensor(PointerSensor));

  // Handle drag end and reorder
  const handleDragEnd = async ({ active, over }) => {
    if (!over || active.id === over.id) return;

    const oldIndex = fields.findIndex((f) => f.id === active.id);
    const newIndex = fields.findIndex((f) => f.id === over.id);
    const reordered = arrayMove(fields, oldIndex, newIndex);

    setFields(reordered);

    // Update backend positions
    const payload = reordered.map((item, index) => ({
      id: item.id,
      position: index,
    }));

    try {
      await axios.post("/employees/fields/reorder/", payload);
    } catch (error) {
      console.error("Failed to save new order:", error);
    }
  };

  return (
    <div className="container mt-4">
      <h5 className="mb-3">ðŸ§² Reorder Form Fields</h5>

      {fields.length === 0 ? (
        <p className="text-muted">No fields defined yet.</p>
      ) : (
        <DndContext
          sensors={sensors}
          collisionDetection={closestCenter}
          onDragEnd={handleDragEnd}
        >
          <SortableContext
            items={fields.map((f) => f.id)}
            strategy={verticalListSortingStrategy}
          >
            {fields.map((field) => (
              <SortableItem key={field.id} field={field} />
            ))}
          </SortableContext>
        </DndContext>
      )}
    </div>
  );
}
