import React, { useEffect, useState } from "react";
import axios from "../api/axios";

import {
  DndContext,
  closestCenter,
  PointerSensor,
  useSensor,
  useSensors,
} from "@dnd-kit/core";
import {
  SortableContext,
  verticalListSortingStrategy,
  arrayMove,
  useSortable,
} from "@dnd-kit/sortable";
import { CSS } from "@dnd-kit/utilities";

// üîÑ Sortable Field Component
function SortableItem({ field, onEdit, onDelete }) {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
  } = useSortable({ id: field.id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
    padding: "10px 15px",
    marginBottom: "10px",
    background: "#f8f9fa",
    border: "1px solid #ccc",
    borderRadius: "5px",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  };

  return (
    <div ref={setNodeRef} style={style} {...attributes} {...listeners}>
      <div>
        <strong>{field.label}</strong>{" "}
        <small className="text-muted">({field.input_type})</small>
        {field.required && <span className="text-danger"> *</span>}
      </div>
      <div>
        <button
          className="btn btn-sm btn-outline-primary me-2"
          onClick={(e) => {
            e.stopPropagation();
            console.log("‚úèÔ∏è Edit button clicked for:", field.label);
            alert("Editing " + field.label);
            onEdit(field);
          }}
        >
          ‚úèÔ∏è
        </button>
        <button
          className="btn btn-sm btn-outline-danger"
          onClick={(e) => {
            e.stopPropagation();
            console.log("‚ùå Delete button clicked for:", field.label);
            alert("Deleting " + field.label);
            onDelete(field.id);
          }}
        >
          ‚ùå
        </button>
      </div>
    </div>
  );
}

// üß† Main Component
export default function FieldBuilder() {
  const [fields, setFields] = useState([]);
  const [form, setForm] = useState({ label: "", input_type: "text", required: false });
  const [editingId, setEditingId] = useState(null);

  const sensors = useSensors(useSensor(PointerSensor));

  useEffect(() => {
    fetchFields();
  }, []);

  const fetchFields = () => {
    axios
      .get("/employees/fields/")
      .then((res) => setFields(res.data))
      .catch((err) => console.error("Failed to fetch fields:", err));
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingId) {
        await axios.put(`/employees/fields/${editingId}/`, form);
        alert("‚úÖ Field updated!");
      } else {
        await axios.post("/employees/fields/", form);
        alert("‚úÖ Field added!");
      }
      setForm({ label: "", input_type: "text", required: false });
      setEditingId(null);
      fetchFields();
    } catch (err) {
      console.error("Save failed", err);
      alert("‚ùå Save failed");
    }
  };

  const handleEdit = (field) => {
    console.log("üõ†Ô∏è Editing field:", field);
    alert("YES - Edit handler triggered");
    setEditingId(field.id);
    setForm({
      label: field.label,
      input_type: field.input_type,
      required: field.required,
    });
  };

  const handleDelete = async (id) => {
    console.log("üóëÔ∏è Deleting field with ID:", id);
    alert("YES - Delete handler triggered");
    try {
      await axios.delete(`/employees/fields/${id}/`);
      alert("‚úÖ Field deleted");
      fetchFields();
    } catch (err) {
      console.error("‚ùå Delete failed:", err);
      alert("‚ùå Delete failed");
    }
  };

  const handleDragEnd = async ({ active, over }) => {
    if (!over || active.id === over.id) return;

    const oldIndex = fields.findIndex((f) => f.id === active.id);
    const newIndex = fields.findIndex((f) => f.id === over.id);
    const newOrder = arrayMove(fields, oldIndex, newIndex);
    setFields(newOrder);

    try {
      await axios.post(
        "/employees/fields/reorder/",
        newOrder.map((f, index) => ({ id: f.id, position: index }))
      );
      console.log("‚úÖ Reorder saved");
    } catch (err) {
      console.error("‚ùå Reorder failed:", err);
    }
  };

  return (
    <div className="container mt-4">
      <h5 className="mb-3">üß≤ Manage & Reorder Form Fields</h5>

      <form onSubmit={handleSubmit} className="row g-2 mb-4">
        <div className="col-md-4">
          <input
            name="label"
            className="form-control"
            placeholder="Field Label"
            value={form.label}
            onChange={handleChange}
            required
          />
        </div>
        <div className="col-md-3">
          <select
            name="input_type"
            className="form-select"
            value={form.input_type}
            onChange={handleChange}
          >
            <option value="text">Text</option>
            <option value="number">Number</option>
            <option value="date">Date</option>
            <option value="password">Password</option>
            <option value="email">Email</option>
            <option value="textarea">Textarea</option>
          </select>
        </div>
        <div className="col-md-2">
          <div className="form-check mt-2">
            <input
              className="form-check-input"
              type="checkbox"
              name="required"
              checked={form.required}
              onChange={handleChange}
            />
            <label className="form-check-label">Required</label>
          </div>
        </div>
        <div className="col-md-3">
          <button type="submit" className="btn btn-success w-100">
            {editingId ? "Update Field" : "Add Field"}
          </button>
        </div>
      </form>

      {fields.length === 0 ? (
        <p className="text-muted">No fields defined yet.</p>
      ) : (
        <DndContext
          sensors={sensors}
          collisionDetection={closestCenter}
          onDragEnd={handleDragEnd}
        >
          <SortableContext
            items={fields.map((f) => f.id)}
            strategy={verticalListSortingStrategy}
          >
            {fields.map((field) => (
              <SortableItem
                key={field.id}
                field={field}
                onEdit={handleEdit}
                onDelete={handleDelete}
              />
            ))}
          </SortableContext>
        </DndContext>
      )}
    </div>
  );
}
