import React, { useState } from 'react';
import Login from './auth/Login';
import DynamicForm from './employees/DynamicForm';
import EmployeeList from './employees/EmployeeList';
import FieldBuilder from './employees/FieldBuilder';
import ChangePassword from './auth/ChangePassword';
import Profile from './auth/Profile';



function App() {
  const [loggedIn, setLoggedIn] = useState(!!localStorage.getItem('access'));

  const handleLogout = () => {
    localStorage.removeItem('access');
    localStorage.removeItem('refresh');
    setLoggedIn(false);
  };

  return (
    <div style={{ padding: '2rem' }}>
      {loggedIn ? (
        <>
          <div style={{ display: 'flex', justifyContent: 'space-between' }}>
            <h1>Welcome</h1>
            <button onClick={handleLogout}>ðŸšª Logout</button>
          </div>
          <Profile />
          <ChangePassword />
          <FieldBuilder />
          <DynamicForm />
          <EmployeeList />
        </>
      ) : (
        <Login onLogin={() => setLoggedIn(true)} />
      )}
    </div>
  );
}

export default App;
