import React, { useEffect, useState } from "react";
import axios from "../api/axios";
import {
  DndContext,
  closestCenter,
  PointerSensor,
  useSensor,
  useSensors,
} from "@dnd-kit/core";
import {
  arrayMove,
  SortableContext,
  useSortable,
  verticalListSortingStrategy,
} from "@dnd-kit/sortable";
import { CSS } from "@dnd-kit/utilities";

// Sortable single item
function SortableItem({ field, onEdit, onDelete }) {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
  } = useSortable({ id: field.id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
    padding: "12px",
    border: "1px solid #ddd",
    borderRadius: "8px",
    marginBottom: "10px",
    backgroundColor: "#f9f9f9",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    cursor: "grab",
  };

  return (
    <div ref={setNodeRef} style={style} {...attributes} {...listeners}>
      <div>
        <strong>{field.label}</strong>{" "}
        <small className="text-muted">({field.input_type})</small>{" "}
        {field.required && <span className="text-danger">*</span>}
      </div>
      <div>
        <button
          className="btn btn-sm btn-outline-primary me-2"
          onClick={() => onEdit(field)}
        >
          ‚úèÔ∏è
        </button>
        <button
          className="btn btn-sm btn-outline-danger"
          onClick={() => onDelete(field.id)}
        >
          ‚ùå
        </button>
      </div>
    </div>
  );
}

export default function FieldBuilder() {
  const [fields, setFields] = useState([]);
  const [form, setForm] = useState({ label: "", input_type: "text", required: false });
  const [editingId, setEditingId] = useState(null);
  const sensors = useSensors(useSensor(PointerSensor));

  useEffect(() => {
    fetchFields();
  }, []);

  const fetchFields = () => {
    axios
      .get("/employees/fields/")
      .then((res) => setFields(res.data))
      .catch((err) => console.error("Failed to fetch fields:", err));
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingId) {
        await axios.put(`/employees/fields/${editingId}/`, form);
      } else {
        await axios.post("/employees/fields/", form);
      }
      setForm({ label: "", input_type: "text", required: false });
      setEditingId(null);
      fetchFields();
    } catch (error) {
      console.error("Failed to save field:", error);
    }
  };

  const handleEdit = (field) => {
    setEditingId(field.id);
    setForm({
      label: field.label,
      input_type: field.input_type,
      required: field.required,
    });
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/employees/fields/${id}/`);
      fetchFields();
    } catch (error) {
      console.error("Failed to delete field:", error);
    }
  };

  const handleDragEnd = async ({ active, over }) => {
    if (!over || active.id === over.id) return;

    const oldIndex = fields.findIndex((f) => f.id === active.id);
    const newIndex = fields.findIndex((f) => f.id === over.id);
    const reordered = arrayMove(fields, oldIndex, newIndex);
    setFields(reordered);

    const payload = reordered.map((item, index) => ({
      id: item.id,
      position: index,
    }));

    try {
      await axios.post("/employees/fields/reorder/", payload);
    } catch (error) {
      console.error("Failed to update field positions:", error);
    }
  };

  return (
    <div className="container mt-4">
      <h5 className="mb-3">üß≤ Manage & Reorder Form Fields</h5>

      {/* Field Form */}
      <form onSubmit={handleSubmit} className="mb-4">
        <div className="row g-2">
          <div className="col-md-4">
            <input
              name="label"
              value={form.label}
              onChange={handleChange}
              className="form-control"
              placeholder="Label"
              required
            />
          </div>
          <div className="col-md-3">
            <select
              name="input_type"
              value={form.input_type}
              onChange={handleChange}
              className="form-select"
            >
              <option value="text">Text</option>
              <option value="number">Number</option>
              <option value="date">Date</option>
              <option value="password">Password</option>
              <option value="email">Email</option>
              <option value="textarea">Textarea</option>
            </select>
          </div>
          <div className="col-md-2">
            <div className="form-check mt-2">
              <input
                type="checkbox"
                className="form-check-input"
                name="required"
                checked={form.required}
                onChange={handleChange}
              />
              <label className="form-check-label">Required</label>
            </div>
          </div>
          <div className="col-md-3">
            <button type="submit" className="btn btn-success w-100">
              {editingId ? "Update Field" : "Add Field"}
            </button>
          </div>
        </div>
      </form>

      {/* List Fields */}
      {fields.length === 0 ? (
        <p className="text-muted">No fields defined yet.</p>
      ) : (
        <DndContext
          sensors={sensors}
          collisionDetection={closestCenter}
          onDragEnd={handleDragEnd}
        >
          <SortableContext
            items={fields.map((f) => f.id)}
            strategy={verticalListSortingStrategy}
          >
            {fields.map((field) => (
              <SortableItem
                key={field.id}
                field={field}
                onEdit={handleEdit}
                onDelete={handleDelete}
              />
            ))}
          </SortableContext>
        </DndContext>
      )}
    </div>
  );
}
