import { useState } from 'react';
import FieldBuilder from '../employees/FieldBuilder';
import DynamicForm from '../employees/DynamicForm';
import EmployeeList from '../employees/EmployeeList';

function EmployeePage() {
  const [showFieldBuilder, setShowFieldBuilder] = useState(false);

  return (
    <div className="container py-4">
      <h2 className="mb-4 border-bottom pb-2">üë®‚Äçüíº Employee Management</h2>

      {/* Section 1: Toggle Field Builder */}
      <div className="mb-4">
        <button
          className="btn btn-outline-primary"
          onClick={() => setShowFieldBuilder((prev) => !prev)}
        >
          {showFieldBuilder ? 'Hide Field Builder' : 'Manage Form Fields'}
        </button>

        {showFieldBuilder && (
          <div className="card p-4 shadow-sm mt-3">
            <FieldBuilder />
          </div>
        )}
      </div>

      {/* Section 2: Create Employee */}
      <div className="mb-5">
        <h5 className="text-success">üìù Create New Employee</h5>
        <div className="card p-4 shadow-sm mt-3">
          <DynamicForm />
        </div>
      </div>

      {/* Section 3: Employee List */}
      <div>
        <h5 className="text-info">üìã All Employees</h5>
        <div className="card p-4 shadow-sm mt-3">
          <EmployeeList />
        </div>
      </div>
    </div>
  );
}

export default EmployeePage;
