import React, { useEffect, useState } from 'react';
import axios from '../api/axios';

function FieldBuilder() {
  const [fields, setFields] = useState([]);
  const [formData, setFormData] = useState({
    label: '',
    input_type: 'text',
    required: false,
  });
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchFields();
  }, []);

  const fetchFields = async () => {
    try {
      const res = await axios.get('employees/fields/');
      setFields(res.data);
    } catch (err) {
      console.error('Error fetching fields', err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('employees/fields/', formData);
      setMessage('‚úÖ Field created!');
      setFormData({ label: '', input_type: 'text', required: false });
      fetchFields();
    } catch (err) {
      console.error('Error creating field', err);
      setMessage('‚ùå Failed to create field');
    }
  };

  return (
    <div style={{ marginBottom: '2rem' }}>
      <h3>Field Builder (Admin)</h3>
      {message && <p>{message}</p>}

      <form onSubmit={handleSubmit}>
        <input
          placeholder="Field Label"
          value={formData.label}
          onChange={(e) => setFormData({ ...formData, label: e.target.value })}
          required
        />
        <select
          value={formData.input_type}
          onChange={(e) => setFormData({ ...formData, input_type: e.target.value })}
        >
          <option value="text">Text</option>
          <option value="number">Number</option>
          <option value="date">Date</option>
        </select>
        <label>
          <input
            type="checkbox"
            checked={formData.required}
            onChange={(e) => setFormData({ ...formData, required: e.target.checked })}
          />
          Required
        </label>
        <button type="submit">Add Field</button>
      </form>

      <hr />

      <h4>Existing Fields</h4>
      <ul>
        {fields.map((field) => (
          <li key={field.id}>
            {field.label} ({field.input_type}) {field.required ? 'üîí required' : ''}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default FieldBuilder;
