import React, { useState, useEffect } from 'react';
import axios from '../api/axios';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

function FieldBuilder() {
  const [fields, setFields] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch fields on mount
  useEffect(() => {
    fetchFields();
  }, []);

  const fetchFields = async () => {
    try {
      const response = await axios.get('/fields/');
      setFields(response.data);
    } catch (error) {
      console.error('Failed to fetch fields:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleDragEnd = async (result) => {
    if (!result.destination) return;

    const reordered = Array.from(fields);
    const [movedItem] = reordered.splice(result.source.index, 1);
    reordered.splice(result.destination.index, 0, movedItem);
    setFields(reordered);

    // Prepare payload
    const updatedPositions = reordered.map((item, index) => ({
      id: item.id,
      position: index,
    }));

    try {
      await axios.post('/fields/reorder/', updatedPositions);
    } catch (error) {
      console.error('Failed to update positions:', error);
    }
  };

  return (
    <div className="container mt-4">
      <h5 className="mb-3">ðŸ§² Reorder Form Fields</h5>

      {loading ? (
        <p>Loading...</p>
      ) : fields.length === 0 ? (
        <p>No fields defined yet.</p>
      ) : (
        <DragDropContext onDragEnd={handleDragEnd}>
          <Droppable droppableId="field-list">
            {(provided) => (
              <ul
                className="list-group"
                {...provided.droppableProps}
                ref={provided.innerRef}
              >
                {fields.map((field, index) => (
                  <Draggable key={field.id} draggableId={field.id.toString()} index={index}>
                    {(provided) => (
                      <li
                        className="list-group-item d-flex justify-content-between align-items-center"
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                      >
                        {field.label}
                        <span className="text-muted">â˜°</span>
                      </li>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </ul>
            )}
          </Droppable>
        </DragDropContext>
      )}
    </div>
  );
}

export default FieldBuilder;
